// import ExpenseItem from "./Components/ExpenseItem";
import { useState } from "react/cjs/react.development";
import Expences from "./Components/Expenses/Expenses";
import NewExpence from "./Components/NewExpences/NewExpences";

function App() {
  const expences = [
    { title: "Car Insurance", date: new Date(2021, 11, 15), price: 294.67 },
    { title: "Bike Insurance", date: new Date(2021, 5, 4), price: 46 },
    { title: "Belenciaga", date: new Date(2021, 3, 14), price: 67 },
    { title: "Karl Lagerfield", date: new Date(2021, 11, 3), price: 89 },
  ];

  const [allExpenseData, setAllExpenseData] = useState(expences);

  const AddExpenseData = (addedExpenseData) => {
    // const newExpenceData = {
    //   ...addedExpenseData,

    // }
    // console.log("App.JS");
    // console.log(addedExpenseData);
    setAllExpenseData(...expences, addedExpenseData);
    console.log(allExpenseData);
  };
  return (
    <div>
      <NewExpence onNewExpenseData={AddExpenseData} />
      {/* <p>This is also visible</p> */}
      <Expences data={expences} />
    </div>
  );
}

export default App;
