import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../Ui/Card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react/cjs/react.development";

const Expences = (props) => {
  const [selectionOfYear, setSelectionOfYear] = useState("2023");

  const YearHandler = (selectedYear) => {
    // console.log(selectedYear);
    setSelectionOfYear(selectedYear);
  };

  let renderItems = <p>No results are found</p>;

  if (filteredItems.length > 0) {
    renderItems = filteredItems.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        date={expense.date}
        price={expense.price}
      />
    ));
  }

  const filteredItems = props.data.filter((expense) => {
    return expense.date.getFullYear().toString() === selectionOfYear;
  });
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selectYear={selectionOfYear}
          onSelectYear={YearHandler}
        />
        {renderItems}
      </Card>
    </div>
  );
};

export default Expences;
