import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../Ui/Card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react/cjs/react.development";

const Expences = (props) => {
  const [selectionOfYear, setSelectionOfYear] = useState("2023");

  const YearHandler = (selectedYear) => {
    // console.log(selectedYear);
    setSelectionOfYear(selectedYear);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          selectYear={selectionOfYear}
          onSelectYear={YearHandler}
        />

        {props.data.map((expense) => (
          <ExpenseItem title={data.title} date={data.date} price={data.price} />
        ))}
        <ExpenseItem
          title={props.data[0].title}
          date={props.data[0].date}
          price={props.data[0].price}
        />
        <ExpenseItem
          title={props.data[1].title}
          date={props.data[1].date}
          price={props.data[1].price}
        />
        <ExpenseItem
          title={props.data[2].title}
          date={props.data[2].date}
          price={props.data[2].price}
        />
        <ExpenseItem
          title={props.data[3].title}
          date={props.data[3].date}
          price={props.data[3].price}
        />
      </Card>
    </div>
  );
};

export default Expences;
